    deletePersona(idPersona) {
      this.macredService.deletePersona(idPersona).pipe(first()).subscribe(response => {
        if (response) {
          this.alertService.success(`Persona eliminada correctamente!`);
          this.ngOnInit();
        } else {
          let message = 'Problemas al eliminar la persona.';
          this.alertService.error(message);
        }
      }, error => {
        let message = 'Problemas de conexión. Detalle: ' + error;
        this.alertService.error(message);
      });
    }

    guardarPersona() {
      this.alertService.clear();
      this.submittedPersonForm = true;

      if (this.formPersona.invalid) {
        return;
      }

      var persona;

      if (this.tipoActualizacion == 'N') {
        persona = this.createPersonaForm();
        persona.codigoCompania = this.userObservable.empresa;
        persona.adicionadoPor = this.userObservable.identificacion;
        persona.fechaAdicion = this.today;
        this.macredService.postPersona(persona).pipe(first()).subscribe(response => {
          if (response) {
            this._personaMacred = response;
            this.alertService.success(`Persona ${this._personaMacred.identificacion} guardado correctamente!`);
            $('#personaModal').modal('hide');
            this.ngOnInit();
          } else {
            let message = 'Problemas al registrar la persona.';
            this.alertService.error(message);
          }
        }, error => {
          let message = 'Problemas de conexión. Detalle: ' + error;
          this.alertService.error(message);
        });
      } else {
        if (this.tipoActualizacion == 'P') {
          persona = this.updatePersonaModal();
        } else if (this.tipoActualizacion = 'C') {
          persona = this.updateCreditoModal();
        }

        persona.modificadoPor = this.userObservable.identificacion;
        persona.fechaModificacion = this.today;
        this.macredService.putPersona(persona).pipe(first()).subscribe(response => {
          if (response) {
            this._personaMacred = response;
            this.alertService.success(`Persona ${this._personaMacred.identificacion} actualizada correctamente!`);

            if (this.tipoActualizacion == 'P') {
              $('#personaModal').modal('hide');
            } else if (this.tipoActualizacion = 'C') {
              $('#infoCreiditiciaModal').modal('hide');
            }

            this.ngOnInit();
          } else {
            let message = 'Problemas al actualizar la persona.';
            this.alertService.error(message);
          }
        }, error => {
          let message = 'Problemas de conexión. Detalle: ' + error;
          this.alertService.error(message);
        });
      }
    }

